mapper.select:
SELECT

FROM sys_user a

WHERE a.id = #{id}

SELECT

FROM sys_user a

WHERE a.login_name = #{loginName} AND a.del_flag = #{DEL_FLAG_NORMAL}

		SELECT

		FROM sys_user a


			JOIN sys_user_role ur ON ur.user_id = a.id AND ur.role_id = #{role.id}

		WHERE a.del_flag = #{DEL_FLAG_NORMAL}


			AND a.id != '1'


            AND a.id = #{id}


            AND a.remarks = #{remarks}


			AND a.login_name like 
					'%'||#{loginName}||'%'
					CONCAT('%', #{loginName}, '%')


			AND a.name like 
					'%'||#{name}||'%'
					CONCAT('%', #{name}, '%')



				ORDER BY ${page.orderBy}


				ORDER BY a.remarks desc, a.id desc,a.create_date desc

mapper.update:
UPDATE sys_user SET 
	password = #{password} 
WHERE id = #{id}

UPDATE sys_user SET 
	login_ip = #{loginIp}, 
	login_Date = #{loginDate} 
WHERE id = #{id}

UPDATE sys_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
